a = int(input('введите первое число: '))
b = int(input('введите первое число: '))
symbol = input(
    "введите необходимую операцию с числами '+', '-', '*', '/', для завершения работы программы введите '0': ")


def operation(a, b):
    if symbol == "+":
        return (a + b)
    elif symbol == "-":
        return (a + b)
    elif symbol == "*":
        return (a * b)
    else:
        if b != 0:
            return (a / b)
        else:
            return 'на ноль делить нельзя'


if symbol == "+" or symbol == "-" or symbol == "*" or symbol == "/":
    print(operation(a, b))
elif symbol == "0":
    print("Выход из программы")
else:
    while not (symbol == "+" or symbol == "-" or symbol == "*" or symbol == "/" or symbol == "0"):
        symbol = input(
            "вы ввели не верный символ, введите '+', '-', '*', '/', для завершения работы программы введите '0': ")
        if symbol == "0":
            print("Выход из программы")
            break

        elif symbol == "+" or symbol == "-" or symbol == "*" or symbol == "/":
            print(operation(a, b))
