import random

count_attempts = 10

number = int(random.random() * 100)

print("компьютер загадал число от 0 до 100 чтобы угадать загаднное компьютером число введите свое, у вас всего 10 попыток")

while count_attempts > 0:
    user_num = int(input("Введите число: "))
    if user_num == number:
        print(f'Вы угадали!!!У вас оставалось {count_attempts} попыток(ки)')
        break
    elif user_num > number:
        print(f"Не угадали, загаданное число меньше '<', у вас осталось {count_attempts} попыток(ки)")
        count_attempts -= 1
    else:
        print(f"Не угадали, загаданное число больше '>', у вас осталось {count_attempts} попыток(ки)")
        count_attempts -= 1
else:
    print('\nВы проиграли, у вас не осталось попыток')
