def n_num(a):
    if a == 1:
        return 1
    return n_num(a - 1) + a


print('проверим верно ли равенство 1+2+...+n = n(n+1)/2')

n = int(input('введите любой натуральное число до 999: '))

if n_num(n) == n * (n + 1) / 2:
    print('да равенство верно')
else:
    print('к сожалению где-то ошибка')
